<div class="login_wrapper">
    <h1>Create player</h1>
    <div class="form-signin">
        <form action="api" method="POST">
            <h2 class="h3 mb-3 fw-normal">name:</h2>
            <div class="form-floating">
                <input name="name" type="text" class="form-control" id="floatingInput" placeholder="name" autocomplete="off" style="background-image: url(&quot;data:image/png;base64,&quot;); background-repeat: no-repeat; background-attachment: scroll; background-size: 16px 18px; background-position: 98% 50%; cursor: auto;">
                <label for="floatingInput">name</label>
            </div>
            <input type="hidden" name="action" value="add_player">
            <button class="w-100 btn btn-lg btn-primary" type="submit">Create!</button>
        </form>
    </div>
</div>